class Solution1 {
public:
    string shortestPalindrome(string s) {
    	if(s.size()<=1) return s;
        int i = s.size()-1;
        string answer = s;
        while(i>=0){
        	if(isPalindrome(s,0,i)){
        		string temp = s.substr(i+1,s.size()-i-1);
        		reverse(temp.begin(),temp.end());
        		//cout << "temp: " << temp << endl;
        		return temp + s;
        	}
        	i--;
        }
    }

    bool isPalindrome(string& s, int l, int r){
		while(l<r){
			if(s[l++]!=s[r--]) return false;
		}
		return true;
	}
};

class Solution {
public:
    string shortestPalindrome(string s) {
        if(s.size()<=1) return s;
        string r = s;
        reverse(r.begin(), r.end());
        string l = s + '#' + r;
        //int p[l.size()];
        //fill_n(&p[0],l.size(),0);

        vector<int> p(l.size(),0);
        for(int i=1;i<l.size();i++){
            int j = p[i-1];
            while(j>0 && l[i]!=l[j])
                j = p[j-1];
            p[i] = (j+= (l[i]==l[j]));
        }
        return r.substr(0, r.size()-p[l.size()-1]) + s;
    }
};

